import LoadingOverlay from 'react-loading-overlay';

const Player = props => (
  <div>
    <h2>{props.desc}</h2>
    <audio id={props.name} preload="auto" controls>
      <source src={"static/audio/" + props.name + ".mp3"} type="audio/mpeg"/>
      <source src={"static/audio/" + props.name + ".ogg"} type="audio/ogg"/>
      Your browser does not support the audio tag.
    </audio>
    <hr />
  </div>
);

function Index() {
  return (
    <LoadingOverlay active spinner text='Loading your content...'>
    <main>
      <link href="https://fonts.googleapis.com/css?family=IM+Fell+French+Canon:400,400i&display=swap" rel="stylesheet"/>
      <img src="static/images/banner.png" alt="The Artefact" />
      <h3>~~~</h3>
      <h3>Sounds of deep, forgotten places to be played as the years drift past.</h3>
      <h3>~~~</h3>
      <hr />
      <Player desc="A Day" name="005_day" />
      <Player desc="A Week" name="010_week" />
      <Player desc="A Month" name="020_month" />
      <Player desc="A Year" name="040_year" />
      <Player desc="A Decade" name="060_decade" />
      <Player desc="A Century" name="120_century" />
      <Player desc="Centuries" name="180_centuries" />
      <Player desc="A Millennium" name="240_millennium" />
      <Player desc="An Age" name="300_age" />
      <Player desc="An Epoch" name="360_epoch" />

      <style jsx global>{`
        body {
        	background-color: #EBCB8B;
          text-align: center;
          font-family: 'IM Fell French Canon', serif;
          color: #2E3440;
        }

        img {
          width: 100%;
        }

        hr {
          width: 50%;
          border: 0;
          height: 2px;
          background-image: linear-gradient(to right, rgba(46, 52, 64, 0), rgba(46, 52, 64, 0.75), rgba(46, 52, 64, 0));
          margin: 1em auto;
        }

        h3 {
        	font-style: italic;
        }
      `}</style>
    </main>
    </LoadingOverlay>
  );
}

export default Index;
