const Player = props => (
  <div>
    <audio id={props.name} controls>
      <source src={"static/audio/" + props.name + ".mp3"} type="audio/mpeg"/>
      <source src={"static/audio/" + props.name + ".ogg"} type="audio/ogg"/>
      Your browser does not support the audio tag.
    </audio>
  </div>
);

const PlayButton = props => (
  <div>
    <button onclick={"document.getElementById(" + props.name + ").play()"}>Play</button>
  </div>
)

//<img src="static/images/banner.png" alt="The Artefact" />
function Index() {
  return (
    <main>

      <Player name="005_day" />
      <PlayButton name="005_day" />
      <style jsx>{`
        img {
          width: 100%;
        }
      `}</style>
    </main>
  );
}

export default Index;
